<?php//модельclass Client extends ar{	public function damir()	{		return 3*3;	}		public function override()	{		if ($this->id == 3) return 'fosv' ;		return "";	}	}class client_controller {	function show()	{		print 7*7;			}}//контроллерfunction clients_index(){	d()->clients  = d()->Client->all;}function clients_show(){	d()->Client->find(url(2))->expand_to_client;	 print d()->client_controller->show();}function clients_edit(){	//Альтернативная запись	$client = new Client();	$client->find(url(2))->expand_to_client;}function clients_update(){	$client = d()->Client->new;	$client->set_attributes($_POST['clients']);		}function action_clients_edit(){	print "Ура! мы увидели POST данные и теперь мы выполняемся!";}function action_mailer($params){	print  "А вот тут отпарвка емейл апо адресу ".d()->adminemail.' и редирект';	exit();	}