<?php//контроллерclass clients_controller {	function index()	{		d()->clients  = d()->Client->all;		print d()->clients_index_tpl();	}		function show()	{		d()->client = d()->Client->find(url(2));		print d()->view();	}	function edit()	{		d()->client = d()->Client->find(url(2));		action('clients#update');		action('mailer');		print d()->view();	}		function update($params)	{		d()->client->title=$params['title'];		d()->client->text=$params['text'];		d()->client->save;		header('Location: /clients/');		exit;	}}function mailer($params){	print  "А вот тут отпарвка емейл апо адресу   и редирект";	var_dump($params);	exit();}