<?php 	function fosv_show()	{		if(d()->validate('send_message')){					d()->Mail->to('user@example.com');			d()->Mail->subject('Письмо с сайта Название Сайта');			d()->Mail->message(d()->mail_tpl());						if(isset($_FILES["file"]) && $_FILES["file"]["tmp_name"]!=''){				d()->Mail->attach($_FILES["file"]);			}			d()->Mail->send();			print "Ваше сообщение отправлено!";		}else{			print d()->view();		}	}  function mail_to_user($to,$header,$text){ 	return mail($to,"=?UTF-8?B?".base64_encode(  $header)."?=", $text,"Content-Type: text/html; charset=\"UTF-8\"");}