<?php//контроллерclass users_controller {	//Пользователь авторизован?	function is_authorised()	{			}		//Регистрация  	function registration()	{		action('users#create');	}		//проверка на занятость ника и другие проверки	function check_registration($params)	{		if( strlen($params['login'])<4) {			d()->add_notice('Имя слишком короткое');			return false;		}	}		 	//Создание пользователя	function create($params)	{		$user=d()->User->new;		$user->login=$params['login'];		$user->password=$params['password'];		$user->save();				header('Location: /');		exit();	}		function authorize()	{			} 	function show()	{		d()->client = d()->Client->find(url(2));		print d()->clients_show_tpl();	}		function edit()	{		d()->client = d()->Client->find(url(2));		action('clients#update');		action('mailer');		print d()->clients_edit();	}		function update($params)	{		d()->client->title=$params['title'];		d()->client->text=$params['text'];		d()->client->save;		header('Location: /clients/');		exit;	}}