<?phpfunction clients_index(){	d()->clients  = d()->Client->all;}function clients_show(){	d()->Client->find(url(2))->expand_to_client;}function clients_edit(){	d()->Client->find(url(2))->expand_to_client;}function input ($params=array()){	$cfo = d()->current_form_object;	$attr='';	if(isset($params['style'])) {		$attr .= ' style="'.$params['style'].'" ';	}		//TODO: проверка на класс error	if(isset($params['class'])) {		$attr .= ' class="'.$params['class'].'" ';	}		if(isset($params['id'])) {		$attr .= ' id="'.$params['id'].'" ';	}		if(isset($params['attr'])) {		$attr .= ' '.$params['attr'].' ';	}		if(isset($params['name'])) {		$attr .= ' name="'.$params['name'].'" ';	} else {		$attr .= ' name="'.$cfo.'['.$params[0].']" ';	}		if(isset($params['type'])) {		$attr .= ' type="'.$params['type'].'" ';	} else {		$attr .= ' type="text" '; 	}		if(isset($params['value'])) {		$attr .= ' value="'.$params['value'].'" ';	}else{		//TODO: проверять если что не так, подствлять из POST		$attr .= ' value= "'.  htmlspecialchars(d($cfo)->{$params[0]}) .'" ';	}		return '<input ' . $attr . '>';}function form ($params=array()) {		d()->current_form_object = $params[0];	return "<form method='POST'>";}function myfunc($p=array()){var_dump($p);}